import { withStyles } from '@material-ui/core';
import React, { Component } from 'react';
import {
    Box,
    Button,
    Typography,
    CardContent,
    CardActions,
    Card,
    Grid,
    AddIcon} from '../../commom/ModulMUI';
class TaskList extends Component {
    render() {
        const {classes , status , tasks} = this.props;
        return (
            <div>
                  <Grid item md={4} xs={12} key={status.value}>
                                <Box mt={2} mb={2}>
                                    <div className={classes.status} >
                                        {status.label}
                                    </div>
                                </Box>

                                <div className={classes.warraperListTask}>
                                    {
                                        tasks.map((task, index) => {
                                            return (
                                                <Card key={task.id} className={classes.card}>
                                                    <CardContent>
                                                        <Grid container spacing={1} justify="space-between">
                                                            <Grid item md={8}>
                                                                <Typography component="h2">{task.title}</Typography>
                                                            </Grid>
                                                            <Grid item md={4}>
                                                                {status.label}
                                                            </Grid>
                                                        </Grid>
                                                    </CardContent>
                                                    <CardActions>
                                                        <Button size="small">
                                                            Mua
                                                        </Button>
                                                    </CardActions>
                                                </Card>
                                            )
                                        })
                                    }
                                </div>
                            </Grid>
            </div>
        );
    }
}

export default withStyles(TaskList);
