import { call, delay, fork, put, take, takeLatest } from "redux-saga/effects";
import * as taskType from "../constants/tasks";
import { fectchTasks } from "../apis/tasks";
import { fetchTasksFailed, fetchTasksSuccess } from "../actions/tasks";
import {hideLoading, showLoading} from '../actions/ui';

function* watchFetchTasks() {
  while (true) {
    try {
      yield take(taskType.FETCH_TASK);
      yield put(showLoading());
      const res = yield call(fectchTasks);
      const { data } = res;
      yield put(fetchTasksSuccess(data));
      yield delay(1000)
      yield put(hideLoading())
    } catch (e) {
      console.log(e);
      yield put(fetchTasksFailed(e));
    }
  }
}

function* fetchFilter({payload}){
  yield delay(500);
  console.log('payload', payload);
}

function* rootSaga() {
  yield fork(watchFetchTasks);
  yield takeLatest(taskType.FETCH_FILTER,fetchFilter)
}

export default rootSaga;
