const styles = (theme) => ({
  drawerPaper: {
    width: 240,
    maxWidth: 240,
    zIndex: 99,
    height: '100%',
    position: 'relative',
  },
  menuLink : {
    textDecoration: 'none',
    color: theme.color.textCorlorDefault
  },
  menuLinkActive : {
    '&>div' : {
      backgroundColor: 'rgba(0, 0, 0, 0.87)'
    }
  }

});
export default styles;
