import React, { Component } from "react";
import { withStyles, Grid, Button, Box } from "@material-ui/core";
import renderTextField from "../../components/FormHelper/TextField";
import { Field } from "redux-form";
import styles from "./styles";
class ModalGlobal extends Component {
  render() {
    const { classes, onClose, invalid , submitting } = this.props;
    return (
      <Grid container>
        <Grid item md={12}>
          <Field
            id="title"
            label="Tiêu đề"
            className={classes.textField}
            margin="normal"
            name="title"
            component={renderTextField}
          />
        </Grid>
        <Grid item md={12}>
          <Field
            id="description"
            label="Mô tả"
            className={classes.textField}
            margin="normal"
            name="description"
            component={renderTextField}
          />
        </Grid>
        <Grid item md={12}>
          <Box display="flex" flexDirection="row-reverse" mt={2}>
            <Box ml={1}>
              <Button variant="contained" onClick={onClose} color="default">
                Huỷ bỏ
              </Button>
            </Box>
            <Button type="submit" disabled= {invalid || submitting} variant="contained" color="primary">
              Lưu lại
            </Button>
          </Box>
        </Grid>
      </Grid>
    );
  }
}

export default withStyles(styles)(ModalGlobal);
