import React, { Component } from "react";
import styles from "./styles";
import { bindActionCreators, compose } from "redux";
import { reduxForm } from "redux-form";
import validate from './validate';
import renderTextField from "../../components/FormHelper/TextField";
import { Field } from "redux-form";
import { withStyles, Grid, Button, Box } from "@material-ui/core";
import { connect } from "react-redux";
import * as taskActions from "../../actions/tasks";
import * as modalActions from "../../actions/modalGlobal";

class TaskForm extends Component {
  handleSubmitForm = (data) => {
    console.log(data);
  };

  onClose = () => {
    const { onModalActions } = this.props;
    const { hideModal } = onModalActions;
    hideModal();
  };
  render() {
    const { classes, invalid , submitting,handleSubmit } = this.props;
    return (
      <form onSubmit={handleSubmit(this.handleSubmitForm)}>
      <Grid container>
      <Grid item md={12}>
        <Field
          id="title"
          label="Tiêu đề"
          className={classes.textField}
          margin="normal"
          name="title"
          component={renderTextField}
        />
      </Grid>
      <Grid item md={12}>
        <Field
          id="description"
          label="Mô tả"
          className={classes.textField}
          margin="normal"
          name="description"
          component={renderTextField}
        />
      </Grid>
      <Grid item md={12}>
        <Box display="flex" flexDirection="row-reverse" mt={2}>
          <Box ml={1}>
            <Button variant="contained" onClick={this.onClose} color="default">
              Huỷ bỏ
            </Button>
          </Box>
          <Button type="submit" disabled= {invalid || submitting} variant="contained" color="primary">
            Lưu lại
          </Button>
        </Box>
      </Grid>
    </Grid>
    </form>
    );
  }
}
const withReduxFrom = reduxForm({
  form: "tasks",
  validate
});
const mapStateToProps = state => {
  return {
    showModal: state.modal.showModal,
  };
}
const mapDispatchToProps = dispatch => {
  return {
    onTaskActions: bindActionCreators(taskActions, dispatch),
    onModalActions: bindActionCreators(modalActions, dispatch),
  };
}
const withConnect = connect(mapStateToProps,mapDispatchToProps);
export default compose(withStyles(styles), withConnect, withReduxFrom)(TaskForm);
