import React, { Component } from "react";
import { withStyles, Modal } from "@material-ui/core";
import ClearIcon from "@material-ui/icons/Clear";
import styles from "./styles";
import { connect } from "react-redux";
import { bindActionCreators, compose } from "redux";
import * as modalActions from '../../actions/modalGlobal';

class TaskForm extends Component {
  render() {
    const {  classes, component } = this.props;
    return (
      <Modal open={this.onOpenForm} onClose={this.onClose}>
        <div className={classes.modal}>
          <div className={classes.header}>
            <span className={classes.title}>Thêm mới</span>
            <ClearIcon className={classes.icon} onClick={this.onClose}></ClearIcon>
          </div>
          <div className={classes.content}>{component}</div>
        </div>
      </Modal>
    );
  }
  onClose = () => {
    const { onModalActions } = this.props;
    const { hideModal } = onModalActions;
    hideModal();
  };
  onOpenForm = () => {
    const { onModalActions } = this.props;
    const { showModal , changeTitleModal } = onModalActions;
    showModal();
    changeTitleModal("Thêm mới công việc")
  };

}

const mapStatetoProps = (state) => {
  return {
    component : state.modal.component
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    onModalAction : bindActionCreators(modalActions , dispatch),
  };
};

const withConnect = connect(mapStatetoProps, mapDispatchToProps);

export default compose(withStyles(styles), withConnect)(TaskForm);
