import * as modalTypes from "../constants/modalGlobal";

const initialState = {
  showModal: false,
  component: null,
  title: "",
};

const modalReducer = (state = initialState, action) => {
  switch (action.type) {
    case modalTypes.SHOW_MODAL: {
      return {
        ...state,
        showModal: true,
      };
    }
    case modalTypes.HIDE_MODAL: {
      return {
        ...state,
        showModal: false,
      };
    }
    case modalTypes.CHANGE_TITLE_MODAL: {
      const { title } = action.modal.payload;
      return {
        ...state,
        title,
      };
    }
    case modalTypes.CHANGE_CONTENT_MODAL: {
      const { component } = action.modal.payload;
      return {
        ...state,
        component,
      };
    }
    default:
      return state;
  }
};

export default modalReducer;
