import React, { Component } from "react";
import { withStyles, Grid, Button, Box } from "@material-ui/core";
import renderTextField from "../../components/FormHelper/TextField";
import { Field, reduxForm } from "redux-form";
import styles from "./styles";
import { compose } from "redux";
class ModalGlobal extends Component {
  render() {
    const { classes, onClose,handleSubmit } = this.props;
    return (
      <form onSubmit={handleSubmit}>
        <Grid container>
          <Grid item md={12}>
            <Field
              id="title"
              label="Tiêu đề"
              className={classes.textField}
              margin="normal"
              name="title"
              component={renderTextField}
            />
          </Grid>
          <Grid item md={12}>
            <Field
              id="description"
              label="Mô tả"
              className={classes.textField}
              margin="normal"
              name="description"
              component={renderTextField}
            />
          </Grid>
          <Grid item md={12}>
            <Box display="flex" flexDirection="row-reverse" mt={2}>
              <Box ml={1}>
                <Button variant="contained" onClick={onClose} color="default">
                  Huỷ bỏ
                </Button>
              </Box>
              <Button variant="contained" color="primary">
                Lưu lại
              </Button>
            </Box>
          </Grid>
        </Grid>
      </form>
    );
  }
}
const withsReduxForm = reduxForm({
  form: "task",
});
export default compose(withStyles(styles), withsReduxForm)(ModalGlobal);
