import { call, delay, fork, put, select, take, takeLatest } from "redux-saga/effects";
import * as taskType from "../constants/tasks";
import { fectchTasks ,addTaks } from "../apis/tasks";
import { fetchTasksFailed, fetchTasksSuccess ,fetchFilterSuccess} from "../actions/tasks";
import {hideLoading, showLoading} from '../actions/ui';
import {STATUS} from '../constants/index';
function* watchFetchTasks() {
  while (true) {
    try {
      yield take(taskType.FETCH_TASK);
      yield put(showLoading());
      const res = yield call(fectchTasks);
      const { data } = res;
      yield put(fetchTasksSuccess(data));
      yield delay(1000)
      yield put(hideLoading())
    } catch (e) {
      console.log(e);
      yield put(fetchTasksFailed(e));
    }
  }
}

function* fetchFilter({payload}){
  yield delay(500);
  const {keyword} = payload;
  const list = yield select(state =>  state.tasks.listTasks)
  const listTaskFiltered = list.filter(task =>
    task.title.trim().toLowerCase().includes(keyword.trim().toLowerCase())
  )
  console.log(listTaskFiltered);
  yield put(fetchFilterSuccess(listTaskFiltered));
}

function* addTask ({payload}){
  yield showLoading();
  const {title , description } = payload;
  const status =STATUS[0].value;
  yield call(addTaks({
    title, description ,status
  }));
}

function* rootSaga() {
  yield fork(watchFetchTasks);
  yield takeLatest(taskType.FETCH_FILTER,fetchFilter)
  yield takeLatest(taskType.ADD_TASK,addTask)

}

export default rootSaga;
