import {
  call,
  delay,
  fork,
  put,
  select,
  take,
  takeLatest,
} from "redux-saga/effects";
import * as taskType from "../constants/tasks";
import { getListTask, addTaks } from "../apis/tasks";
import {
  fetchTasksFailed,
  fetchTasksSuccess,
  fetchFilterSuccess,
  addTasksSuccess,
  addTasksFailed,
  fetchTasks
} from "../actions/tasks";
import { hideLoading, showLoading } from "../actions/ui";
import { hideModal } from "../actions/modalGlobal";

import { STATUS } from "../constants/index";

function* watchFetchTasks() {
    try {
      yield put(showLoading());
      const result = yield put(fetchTasks);
      const {keyword} = result.payload;
      const res = yield call(getListTask({
        g : keyword
      }));
      const { data } = res;
      yield put(fetchTasksSuccess(data));
      yield delay(1000);
      yield put(hideLoading());
    } catch (e) {
      console.log(e);
      yield put(fetchTasksFailed(e));
    }
}

function* fetchFilter({ payload }) {
  yield delay(500);
  const {keyword} =  payload.data;
  console.log(payload);
  //const action = yield put()
  //yield put(fetchFilterSuccess(listTaskFiltered));
}

function* addTask({ payload }) {
  try {
    yield put(showLoading());
    const { title, description } = payload.data;
    const res = yield call(addTaks, {
      title,
      description,
      status: STATUS[0].value,
    });
    yield put(addTasksSuccess(res.data));
    yield put(hideModal());
    yield delay(1000);
    yield put(hideLoading());

  } catch (error) {
    console.log(error);
    yield put(addTasksFailed(error));
  }
}

