import React, { Component } from 'react';
import styles from './styles';
import { withStyles } from '@material-ui/core';
import { STATUS } from '../../constants/index';
import TaskList from '../../containers/TaskList';
import {Grid ,Button , AddIcon} from '../../commom/ModulMUI';
import TaskForm from '../TaskForm';
import { connect } from 'react-redux';


class TaskBoard extends Component {

    state = {
        onOpenForm : false
    }

    render() {
        const { classes } = this.props;
        return (
            <div className={classes.taskboard}>
                <Button variant="contained" color="primary" onClick={this.onOpenForm}>
                    <AddIcon />Thêm mới công việc
                </Button>

                {this.renderTaskBoard()}
                {this.renderTaskForm()}
            </div>
        );
    }
    onOpenForm = () => {
        this.setState({
            onOpenForm : true
        })
    }

    onClose = () => {
        this.setState({
            onOpenForm : false
        })
    }
    renderTaskForm = () => {
        let html = null;
        html = (
            <TaskForm
                onClose = {this.onClose}
                onOpenForm = {this.state.onOpenForm}
             />
        )
        return html;
    }
    renderTaskBoard = () => {
        const {listTask} = this.props;
        let html = null;
        html = (
            <Grid container spacing={2}>
                {
                    STATUS.map((status) => {
                        const listTaskFilter = listTask.filter(tasks =>
                            tasks.status === status.value
                        )
                        return <TaskList
                                key ={status.value}
                                status = {status}
                                tasks = {listTaskFilter}
                        />
                    })
                }
            </Grid>
        )
        return html;
    }
}
const mapStateToProps = state => {
  return {
    listTask : this.state.tasks
  }
}
const mapDispatchToProps = (dispatch ,props) => {
  return {

  }
}
export default withStyles(styles)(connect(mapStateToProps, mapDispatchToProps)(TaskBoard));
