import React, { Component } from 'react';
import styles from './styles';
import { withStyles } from '@material-ui/core';
import { STATUS } from '../../constants/index';
import TaskList from '../../containers/TaskList';
import {Grid ,Button , AddIcon} from '../../commom/ModulMUI';
import TaskForm from '../TaskForm';

const listTask = [
    {
        id: 1,
        title: "Play game",
        description: "Ở nhà",
        status: 0
    },
    {
        id: 2,
        title: "Play Football",
        description: "Ở Truờng",
        status: 2
    }, {
        id: 3,
        title: "Play Girl",
        description: "Ở Hotel",
        status: 1
    }
]
class TaskBoard extends Component {

    state = {
        onOpenForm : false
    }

    render() {
        const { classes } = this.props;
        return (
            <div className={classes.taskboard}>
                <Button variant="contained" color="primary">
                    <AddIcon />Thêm mới công việc
                </Button>

                {this.renderTaskBoard()}
                {this.renderTaskForm()}
            </div>
        );
    }

    onClose = () => {
        this.setState({
            onOpenForm : false
        })
    }
    renderTaskForm = () => {
        let html = null;
        html = (
            <TaskForm
                onClose = {this.onClose}
                onOpenForm = {onOpenForm}
             />
        )
        return html;
    }
    renderTaskBoard = () => {
        let html = null;
        html = (
            <Grid container spacing={2}>
                {
                    STATUS.map((status) => {
                        const listTaskFilter = listTask.filter(tasks =>
                            tasks.status === status.value
                        )
                        return <TaskList
                                key ={status.value}
                                status = {status}
                                tasks = {listTaskFilter}
                        />
                    })
                }
            </Grid>
        )
        return html;
    }
}

export default withStyles(styles)(TaskBoard);
