import { withStyles } from "@material-ui/core";
import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { Box, Grid } from "../../commom/ModulMUI";
import * as taskActions from "../../actions/tasks";
import TaskItem from "../../containers/TaskItem";
import styles from "../TaskBorad/styles";
class TaskList extends Component {
  componentDidMount() {
    const { onTaskActions } = this.props;
    const { fetchTasks } = onTaskActions;
    fetchTasks();
  }
  render() {
    const { classes, status, tasks } = this.props;
    return (
      <Fragment>
        <Grid item md={4} xs={12} key={status.value}>
          <Box mt={2} mb={2}>
            <div className={classes.status}>{status.label}</div>
          </Box>

          <div className={classes.warraperListTask}>
            {tasks.map((tasks) => {
              return <TaskItem key={tasks.id} task={tasks} status={status} />;
            })}
          </div>
        </Grid>
      </Fragment>
    );
  }
}
const mapStateToProps = null;

const mapDispatchToProps = (dispatch) => {
  return {
    onTaskActions: bindActionCreators(taskActions, dispatch),
  };
};
export default withStyles(styles)(
  connect(mapStateToProps, mapDispatchToProps)(TaskList)
);
