import React, { Component } from "react";
import styles from "./styles";
import { withStyles } from "@material-ui/core";
import { STATUS } from "../../constants/index";
import TaskList from "../../components/TaskList";
import { Grid, Button } from "@material-ui/core";
import { Add as AddIcon } from "@material-ui/icons";
import TaskForm from "../../components/TaskForm";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import * as taskActions from "../../actions/tasks";
import SearchBox from "../../components/SearchBox";
class TaskBoard extends Component {
  componentDidMount() {
    const { onTaskActions } = this.props;
    const { fetchTasks } = onTaskActions;
    fetchTasks();
  }
  state = {
    onOpenForm: false,
  };

  render() {
    const { classes } = this.props;
    return (
      <div className={classes.taskboard}>
        <Button variant="contained" color="primary" onClick={this.onOpenForm}>
          <AddIcon />
          Thêm mới công việc
        </Button>
        {this.renderSearchBox()}
        {this.renderTaskBoard()}
        {this.renderTaskForm()}
      </div>
    );
  }
  handleFilter = (e) => {
    const { onTaskActions } = this.props;
    const { fetchFilter } = onTaskActions;
    const { value } = e.target;
    fetchFilter(value);
  };
  renderSearchBox = () => {
    let html = null;
    html = <SearchBox handlChange={this.handleFilter} />;
    return html;
  };
  onOpenForm = () => {
    this.setState({
      onOpenForm: true,
    });
  };

  onClose = () => {
    this.setState({
      onOpenForm: false,
    });
  };
  renderTaskForm = () => {
    let html = null;
    html = (
      <TaskForm onClose={this.onClose} onOpenForm={this.state.onOpenForm} />
    );
    return html;
  };
  renderTaskBoard = () => {
    let { listTask } = this.props;

    let html = null;
    html = (
      <Grid container spacing={2}>
        {STATUS.map((status) => {
          const listTaskFilter = listTask.filter(
            (tasks) => tasks.status === status.value
          );
          return (
            <TaskList
              key={status.value}
              status={status}
              tasks={listTaskFilter}
            />
          );
        })}
      </Grid>
    );
    return html;
  };
}
const mapStateToProps = (state) => {
  return {
    listTask: state.tasks.listTasks,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    onTaskActions: bindActionCreators(taskActions, dispatch),
  };
};

export default withStyles(styles)(
  connect(mapStateToProps, mapDispatchToProps)(TaskBoard)
);
