import React, { Component } from 'react';
import styles from './styles';
import { withStyles } from '@material-ui/core';
import { STATUS } from '../../constants/index';
import {
    Box,
    Button,
    Typography,
    CardContent,
    CardActions,
    Card,
    Grid,
    AddIcon} from '../../commom/ModulMUI';

const listTask = [
    {
        id: 1,
        title: "Play game",
        desciption: "Ở nhà",
        status: 0
    },
    {
        id: 2,
        title: "Play Football",
        desciption: "Ở Truờng",
        status: 2
    }, {
        id: 3,
        title: "Play Girl",
        desciption: "Ở Hotel",
        status: 1
    }
]
class TaskBoard extends Component {
    render() {
        const { classes } = this.props;
        return (
            <div className={classes.taskboard}>
                <Button variant="contained" color="primary">
                    <AddIcon />Thêm mới công việc
                </Button>

                {this.renderTaskBoard()}

            </div>
        );
    }
    renderTaskBoard = () => {
        const { classes } = this.props;
        let html = null;
        html = (

            <Grid container spacing={2}>
                {
                    STATUS.map((status, index) => {
                        const listTaskFilter = listTask.filter(tasks =>
                            tasks.status === status.value
                        )
                        return (

                            <Grid item md={4} xs={12} key={status.value}>
                                <Box mt={2} mb={2}>
                                    <div className={classes.status} >
                                        {status.label}
                                    </div>
                                </Box>

                                <div className={classes.warraperListTask}>
                                    {
                                        listTaskFilter.map((task, index) => {
                                            return (
                                                <Card key={task.id} className={classes.card}>
                                                    <CardContent>
                                                        <Grid container spacing={1} justify="space-between">
                                                            <Grid item md={8}>
                                                                <Typography component="h2">{task.title}</Typography>
                                                            </Grid>
                                                            <Grid item md={4}>
                                                                {status.label}
                                                            </Grid>
                                                        </Grid>
                                                    </CardContent>
                                                    <CardActions>
                                                        <Button size="small">
                                                            Mua
                                                        </Button>
                                                    </CardActions>
                                                </Card>
                                            )
                                        })
                                    }
                                </div>
                            </Grid>
                        )
                    })
                }
            </Grid>
        )
        return html;
    }
}

export default withStyles(styles)(TaskBoard);
