import React, { Component } from "react";
import { withStyles, Modal } from "@material-ui/core";
import ClearIcon from "@material-ui/icons/Clear";
import styles from "./styles";
import { compose } from "redux";
import { reduxForm,Field } from "redux-form";
import {
  Grid,
  Button,
  Box,
} from "@material-ui/core";
import renderTextField from '../../components/FormHelper/TextField';

class TaskForm extends Component {
  handleSubmitForm = (data) => {
    console.log(data);
  }
  render() {
    const { classes, onClose, component, title, showModal ,handleSubmit} = this.props;
    return (
      <form onSubmit={handleSubmit(this.handleSubmitForm)}>
        <Modal open={showModal} onClose={onClose}>
          <div className={classes.modal}>
            <div className={classes.header}>
              <span className={classes.title}>{title}</span>
              <ClearIcon className={classes.icon} onClick={onClose}></ClearIcon>
            </div>
            <div className={classes.content}><Grid container>
                <Grid item md={12}>
                  <Field
                    id="title"
                    label="Tiêu đề"
                    className={classes.textField}
                    margin="normal"
                    name="title"
                    component={renderTextField}
                  />
                </Grid>
                <Grid item md={12}>
                  <Field
                    id="description"
                    label="Mô tả"
                    className={classes.textField}
                    margin="normal"
                    name="description"
                    component={renderTextField}
                  />
                </Grid>
                <Grid item md={12}>
                  <Box display="flex" flexDirection="row-reverse" mt={2}>
                    <Box ml={1}>
                      <Button
                        variant="contained"
                        onClick={onClose}
                        color="default"
                      >
                        Huỷ bỏ
                      </Button>
                    </Box>
                    <Button type="submit" variant="contained" color="primary">
                      Lưu lại
                    </Button>
                  </Box>
                </Grid>
              </Grid></div>
          </div>
        </Modal>
      </form>
    );
  }
}
const withReduxFrom = reduxForm({
  form: "tasks",
});
export default compose(withStyles(styles), withReduxFrom)(TaskForm);
