import React, { Component } from "react";
import { withStyles, Modal } from "@material-ui/core";
import ClearIcon from "@material-ui/icons/Clear";
import styles from "./styles";
import { compose } from "redux";
import { Field, reduxForm } from "redux-form";

class TaskForm extends Component {
  render() {
    const { classes, onClose, component, title, showModal ,handleSubmit,handleSubmitForm} = this.props;
    return (
      <form onSubmit={handleSubmit(handleSubmitForm)}>
        <Modal open={showModal} onClose={onClose}>
          <div className={classes.modal}>
            <div className={classes.header}>
              <span className={classes.title}>{title}</span>
              <ClearIcon className={classes.icon} onClick={onClose}></ClearIcon>
            </div>
            <div className={classes.content}>{component}</div>
          </div>
        </Modal>
      </form>
    );
  }
}
const withReduxFrom = reduxForm({
  form: "tasks",
});
export default compose(withStyles(styles), withReduxFrom)(TaskForm);
